import "./App.css";
import Axios from "axios";
import { useEffect, useState } from "react";
import './css/bulma.min.css';



function App() {
  const [catFact, setCatFact] = useState("");

  const fetchExcuse = (excuse) => {
    Axios.get("https://catfact.ninja/facts").then(
      (res) => {
        setCatFact(res.data[0].excuse);
      }
    );
  };

  return (
    <div className="App">
      <div className="container">
        <div className="columns">
          <div className="column"></div>
          <div className="column">
              <nav className="panel">
                <p className="panel-heading">FETCH FROM API</p>
                <div className="panel-block">
                  <p className="control has-icons-left">
                    <input id="todo-input" className="input" type="text" placeholder="Add a task"/>
                  </p>
                </div>


                <div className="panel-block reset">
                  <button className="button is-link is-outlined is-fullwidth"  onClick={() => fetchExcuse("party")}>
                    Get CATFACT
                  </button>
                  <p>{catFact}</p>
                </div>
              </nav>
          </div>
          <div className="column"></div>
        </div>
      </div>
    </div>
  );
}

export default App;
